Структура { &quot;attrs&quot;: { &quot;removable&quot;: &quot;(boolean)&quot; }, &quot;defaultValue&quot;: &quot;(any)&quot;, &quot;entityId&quot;: &quot;(number)&quot;, &quot;formHorizontal&quot;: &quot;(boolean)&quot;, &quot;help&quot;: &quot;(string)&quot;, &quot;icon&quot;: &quot;(string)&quot;, &quot;id&quot;: &quot;(string)&quot;, &quot;label&quot;: &quot;(string)&quot;, &quot;name&quot;: &quot;me-tabs&quot;, &quot;offset&quot;: &quot;(number)&quot;, &quot;selfDepends&quot;: &quot;(boolean)&quot;, &quot;span&quot;: &quot;(number)&quot;, &quot;value&quot;: &quot;(string)&quot; }

